mapper.update:
UPDATE board_comment
SET
comment = #{comment},
mod_date = #{modDate},
mod_ip = #{modIp},
mod_member = #{modMember}
WHERE id = #{id}

    select id from board_comment WHERE id = #{id};

UPDATE board_comment
SET
del_date = #{delDate},
del_ip = #{delIp},
del_member = #{delMember}
WHERE id = #{id}

    select id from board_comment WHERE id = #{id};

UPDATE board
SET
    title = COALESCE(#{title},title),
    content = COALESCE(#{content},content),
    mod_date = #{modDate},
    mod_ip = #{modIp},
    mod_member = #{modMember}
WHERE id = #{id}

    select id from board WHERE id = #{id};
